def convert_base(num, base): 
    if base > 9 or base < 1 or num < 0:
        return None 
    if num == 0:
        if base == 1:
            return ""
        return "0"
    res = ""
    if base == 1:
        return "1"*num
    while num > 0:
        remainder = num % base
        res = str(remainder) + res
        num //= base
    return res

